
export type Difficulty = 'Easy' | 'Medium' | 'Hard';
export type QuestionType = 'SINGLE' | 'TRUE_FALSE';

export interface QuizQuestion {
  id: number;
  category: string;
  difficulty: Difficulty;
  type: QuestionType;
  question: string;
  options: string[];
  correctAnswer: string;
  explanation: string;
}

// Helper to generate questions quickly
const q = (id: number, cat: string, diff: Difficulty, ques: string, opts: string[], ans: string, exp: string): QuizQuestion => ({
  id, category: cat, difficulty: diff, type: 'SINGLE', question: ques, options: opts, correctAnswer: ans, explanation: exp
});

const tf = (id: number, cat: string, diff: Difficulty, ques: string, ans: boolean, exp: string): QuizQuestion => ({
  id, category: cat, difficulty: diff, type: 'TRUE_FALSE', question: ques, options: ["正确 (True)", "错误 (False)"], correctAnswer: ans ? "正确 (True)" : "错误 (False)", explanation: exp
});

